import { useParams, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const EventDetailPage = () => {
  const { eventId } = useParams();
  const navigate = useNavigate();
  const customer = JSON.parse(sessionStorage.getItem("active-customer"));
  const customer_jwtToken = sessionStorage.getItem("customer-jwtToken");
  const [event, setEvent] = useState({});

  useEffect(() => {
    const getEvent = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8080/api/event/fetch?eventId=${eventId}`
        );
        setEvent(response.data.events[0]);
      } catch (error) {
        toast.error("Failed to load event details");
      }
    };
    getEvent();
  }, [eventId]);

  const formatDateFromEpoch = (epochTime) => {
    return new Date(Number(epochTime)).toLocaleString();
  };

  const bookEventPage = () => {
    if (!customer) {
      toast.warn("Please login as a customer to book an event!");
    } else {
      navigate("/event/booking/page", { state: event });
    }
  };

  return (
    <div className="container mx-auto p-6">
      <div className="bg-white shadow-lg rounded-lg overflow-hidden">
        <div className="grid md:grid-cols-2">
          {/* Event Image */}
          <div className="p-6 flex justify-center items-center">
            <img
              src={`http://localhost:8080/api/event/${event.image}`}
              alt="Event"
              className="rounded-lg w-full h-auto max-h-96 object-cover"
            />
          </div>
          {/* Event Details */}
          <div className="p-6">
            <h2 className="text-3xl font-semibold text-gray-800">{event.name}</h2>
            <p className="text-gray-600 mt-2">{event.description}</p>
            <p className="text-gray-700 mt-2"><b>Location:</b> {event.location}</p>
            <p className="text-gray-700 mt-2"><b>Venue:</b> {event.venueName} ({event.venueType})</p>
            <div className="mt-4">
              <span className="bg-blue-100 text-blue-800 text-sm font-medium px-4 py-1 rounded-full">
                {event.category}
              </span>
            </div>
          </div>
        </div>
      </div>

      {/* Ticket Information */}
      <div className="mt-6 bg-white shadow-lg rounded-lg p-6">
        <h3 className="text-2xl font-semibold text-gray-800">Ticket Details</h3>
        <div className="grid md:grid-cols-3 gap-4 mt-4">
          <p><b>Total Tickets:</b> {event.noOfTickets}</p>
          <p><b>Available Tickets:</b> {event.availableTickets}</p>
          <p><b>Ticket Price:</b> ₹{event.ticketPrice}</p>
        </div>
        <div className="grid md:grid-cols-2 gap-4 mt-4">
          <p><b>Event Start:</b> {formatDateFromEpoch(event.startDate)}</p>
          <p><b>Event End:</b> {formatDateFromEpoch(event.endDate)}</p>
        </div>
        <div className="mt-6 flex justify-center">
          <button
            className="bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700 transition duration-200"
            onClick={bookEventPage}
          >
            Book Event
          </button>
        </div>
      </div>
      <ToastContainer />
    </div>
  );
};

export default EventDetailPage;
