import { useState, useEffect } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import { useNavigate } from "react-router-dom";

const AddEventForm = () => {
  const [categories, setCategories] = useState([]);
  const [selectedImage1, setSelectImage1] = useState(null);
  const [event, setEvent] = useState({});
  const [errors, setErrors] = useState({}); // Stores validation errors

  const manager = JSON.parse(sessionStorage.getItem("active-manager"));
  const manager_jwtToken = sessionStorage.getItem("manager-jwtToken");

  let navigate = useNavigate();

  useEffect(() => {
    const getAllCategories = async () => {
      try {
        const response = await axios.get(
          "http://localhost:8080/api/event/category/fetch/all"
        );
        setCategories(response.data.categories || []);
      } catch (error) {
        console.error("Error fetching categories:", error);
      }
    };
    getAllCategories();
  }, []);

  const handleInput = (e) => {
    setEvent({ ...event, [e.target.name]: e.target.value });
  };

  const validateForm = () => {
    let errors = {};

    if (!event.name) errors.name = "Event title is required.";
    if (!event.description) errors.description = "Event description is required.";
    if (!event.categoryId || event.categoryId === "0") errors.categoryId = "Select a category.";
    if (!event.venueName) errors.venueName = "Venue name is required.";
    if (!event.venueType) errors.venueType = "Select a venue type.";
    if (!event.location) errors.location = "Event location is required.";
    if (!event.noOfTickets || event.noOfTickets <= 0) errors.noOfTickets = "Number of tickets must be greater than zero.";
    if (!event.ticketPrice || event.ticketPrice <= 0) errors.ticketPrice = "Ticket price must be a positive number.";
    if (!event.startDate) errors.startDate = "Event start date is required.";
    if (!event.endDate) errors.endDate = "Event end date is required.";
    if (event.startDate && event.endDate && new Date(event.startDate) >= new Date(event.endDate)) {
      errors.endDate = "End date must be after start date.";
    }
    if (!selectedImage1) errors.image = "Please upload an event image.";

    setErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const saveEvent = (e) => {
    e.preventDefault();

    if (!validateForm()) {
      toast.error("Please fix the errors before submitting.", { position: "top-center" });
      return;
    }

    const formData = new FormData();
    formData.append("name", event.name);
    formData.append("description", event.description);
    formData.append("venueName", event.venueName);
    formData.append("venueType", event.venueType);
    formData.append("location", event.location);
    formData.append("image", selectedImage1);
    formData.append("noOfTickets", event.noOfTickets);
    formData.append("startDate", new Date(event.startDate).getTime());
    formData.append("endDate", new Date(event.endDate).getTime());
    formData.append("ticketPrice", event.ticketPrice);
    formData.append("categoryId", event.categoryId);
    formData.append("managerId", manager.id);

    axios
      .post("http://localhost:8080/api/event/add", formData, {
        headers: { Authorization: "Bearer " + manager_jwtToken },
      })
      .then((resp) => {
        let response = resp.data;
        if (response.success) {
          toast.success(response.responseMessage, { position: "top-center" });
          setTimeout(() => navigate("/home"), 2000);
        } else {
          toast.error(response.responseMessage, { position: "top-center" });
        }
      })
      .catch((error) => {
        console.error(error);
        toast.error("Server error! Please try again later.", { position: "top-center" });
      });
  };

  return (
    <div>
      <div className="mt-2 d-flex aligns-items-center justify-content-center mb-4">
        <div className="card form-card shadow-lg" style={{ width: "60rem" }}>
          <div className="container-fluid">
            <div className="card-header bg-color custom-bg-text mt-2 text-center" style={{ borderRadius: "1em", height: "45px" }}>
              <h5 className="card-title">Add Event</h5>
            </div>
            <div className="card-body text-color">
              <form className="row g-3" onSubmit={saveEvent}>
                <div className="col-md-6 mb-3">
                  <label className="form-label"><b>Event Title</b></label>
                  <input type="text" className="form-control" name="name" onChange={handleInput} value={event.name || ""} />
                  {errors.name && <span className="text-danger">{errors.name}</span>}
                </div>

                <div className="col-md-6 mb-3">
                  <label className="form-label"><b>Event Description</b></label>
                  <input type="text" className="form-control" name="description" onChange={handleInput} value={event.description || ""} />
                  {errors.description && <span className="text-danger">{errors.description}</span>}
                </div>

                <div className="col-md-6 mb-3">
                  <label className="form-label"><b>Event Category</b></label>
                  <select name="categoryId" onChange={handleInput} className="form-control">
                    <option value="">Select Event Category</option>
                    {categories.map((category) => (
                      <option key={category.id} value={category.id}>{category.name}</option>
                    ))}
                  </select>
                  {errors.categoryId && <span className="text-danger">{errors.categoryId}</span>}
                </div>

                {/* Other input fields with similar validation messages */}

                <div className="col-md-6 mb-3">
                  <label className="form-label"><b>Select Event Image</b></label>
                  <input className="form-control" type="file" onChange={(e) => setSelectImage1(e.target.files[0])} />
                  {errors.image && <span className="text-danger">{errors.image}</span>}
                </div>

                <div className="d-flex aligns-items-center justify-content-center mb-2">
                  <button type="submit" className="btn bg-color custom-bg-text">Add Event</button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddEventForm;
