import { useParams, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const EventDetailPage = () => {
  const { eventId } = useParams();
  const navigate = useNavigate();

  const customer = JSON.parse(sessionStorage.getItem("active-customer"));
  const customer_jwtToken = sessionStorage.getItem("customer-jwtToken");

  const [event, setEvent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchEvent = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/event/fetch?eventId=${eventId}`);
        if (response.data.events && response.data.events.length > 0) {
          setEvent(response.data.events[0]);
        } else {
          setError("Event not found.");
        }
      } catch (err) {
        setError("Failed to fetch event details.");
      } finally {
        setLoading(false);
      }
    };

    fetchEvent();
  }, [eventId]);

  const formatDateFromEpoch = (epochTime) => {
    if (!epochTime) return "N/A";
    const date = new Date(Number(epochTime));
    return date.toLocaleString();
  };

  const handleBooking = () => {
    if (!customer) {
      toast.warning("Please log in to book an event.");
      return;
    }
    navigate("/event/booking/page", { state: event });
  };

  if (loading) {
    return <div className="text-center text-lg font-semibold mt-10">Loading event details...</div>;
  }

  if (error) {
    return <div className="text-center text-red-500 font-semibold mt-10">{error}</div>;
  }

  return (
    <div className="max-w-4xl mx-auto my-10 p-6 bg-white shadow-lg rounded-lg">
      {/* Event Header */}
      <div className="flex items-center space-x-4 border-b pb-4">
        <img
          src={`http://localhost:8080/api/event/${event.image}`}
          alt="Event"
          className="w-32 h-32 object-cover rounded-lg shadow-md"
        />
        <div>
          <h2 className="text-2xl font-bold text-gray-800">{event.name}</h2>
          <p className="text-gray-600">{event.description || "No description available"}</p>
          <p className="text-sm text-gray-500 mt-1">📍 {event.location}</p>
        </div>
      </div>

      {/* Venue Details */}
      <div className="mt-5">
        <h3 className="text-xl font-semibold text-gray-700">🏛️ Venue Details</h3>
        <div className="mt-2 text-gray-600">
          <p><strong>Venue Name:</strong> {event.venueName || "N/A"}</p>
          <p><strong>Venue Type:</strong> {event.venueType || "N/A"}</p>
        </div>
      </div>

      {/* Ticket Details */}
      <div className="mt-5">
        <h3 className="text-xl font-semibold text-gray-700">🎟️ Ticket Details</h3>
        <div className="grid grid-cols-2 gap-4 mt-2 text-gray-600">
          <p><strong>Total Tickets:</strong> {event.noOfTickets || "N/A"}</p>
          <p><strong>Available Tickets:</strong> {event.availableTickets || "N/A"}</p>
          <p><strong>Ticket Price:</strong> ₹{event.ticketPrice || "N/A"}</p>
          <p><strong>Event Start Time:</strong> {formatDateFromEpoch(event.startDate)}</p>
          <p><strong>Event End Time:</strong> {formatDateFromEpoch(event.endDate)}</p>
        </div>
      </div>

      {/* Book Event Button */}
      <div className="mt-6 flex justify-center">
        <button
          onClick={handleBooking}
          className="bg-blue-600 hover:bg-blue-700 text-white font-semibold px-6 py-2 rounded-lg shadow-md transition"
        >
          Book Event
        </button>
      </div>

      <ToastContainer />
    </div>
  );
};

export default EventDetailPage;
